  /* URI 参数匹配 */
  function getQueryString(name) {
    let reg = new RegExp('(^|&)' + name + '=([^&]*)(&|$)');
    let r = window.location.search.substr(1).match(reg);
    if (r != null) return unescape(r[2]);
    return null;
  }

  // 验证 ERP 员工编号
  function validateUserID(user_id) {
    let user_id_pattern = new RegExp('[1-9]{1}[0-9]{7}');
    return user_id_pattern.test(user_id);
  }

  /* 获取用户及课程信息 */
  function getBasicInfo(name) {
    switch (name) {
      case 'user_id':
        return validateUserID(getQueryString('us_id'))
          ? getQueryString('us_id')
          : null;
        break;
      case 'lesson_id':
        return getQueryString('le_id')
          ? getQueryString('le_id')
          : getQueryString('tl_id')
          ? getQueryString('tl_id')
          : null;
        break;
      default:
        return null;
    }
  }

  /* 订阅课程 */
  function subscribeLesson(lesson_id, category = 1, seled = 1) {
    // 方案 1 - 课程界面
    let data =
      'leid=' + lesson_id + '&category=' + category + '&seled=' + seled;
    $.ajax({
      type: 'post',
      url: '/www/command/XYxkzxControl?flag=chooseLesson_sysc',
      data: data,
      dataType: 'json',
      async: true,
    });

    //方案 2 - 学院界面
    $.ajax({
      type: 'post',
      url: '/www/command/CollegeControl?flag=chooseLesson&le_id=' + lesson_id,
    });
  }

  /* 退订课程 */
  function unsubscribeLesson(lesson_id, category = 1, seled = 0) {
    // 方案 1 - 课程界面
    let data =
      'leid=' + lesson_id + '&category=' + category + '&seled=' + seled;
    $.ajax({
      type: 'post',
      url: '/www/command/XYxkzxControl?flag=dropLesson_sysc',
      data: data,
      dataType: 'json',
      async: true,
    });

    // 方案 2 - 学院界面
    $.ajax({
      type: 'post',
      url: '/www/command/CollegeControl?flag=dropLesson&le_id=' + lesson_id,
    });
  }

  /* 打开课程 */
  function enterLesson(lesson_id, type = 4, preview = 1, lesson_type = 1) {
    if (type == 4 || type == 2) {
      //进入或预览单一入口 scorm课件
      let lesson_url =
        'http://wldx.sgcc.com.cn/www/command/LessonAction?flag=study&le_id=' +
        lesson_id +
        '&type=' +
        type +
        '&preview=' +
        preview +
        '&lessontype=' +
        lesson_type;
      GM.openInTab(lesson_url, true);
    } else if (type == 5 && preview == 0) {
      // 预览aicc课程
      let lesson_url =
        'http://wldx.sgcc.com.cn/www/command/LessonAction?flag=enterAiccCourse&le_id=' +
        lesson_id +
        '&tl_id=' +
        lesson_id +
        '&preview=' +
        preview +
        '&type=' +
        type;
      GM.openInTab(lesson_url, true);
    } else if (type == 5 && preview == 1) {
      //进入aicc课程
      let lesson_url =
        'http://wldx.sgcc.com.cn/www/command/LessonAction?flag=study&le_id=' +
        lesson_id +
        '&type=' +
        type +
        '&preview=' +
        preview +
        '&tl_id=' +
        lesson_id;
      GM.openInTab(lesson_url, true);
    }
  }

  /* 提交课程进度 */
  function submitProgress(lesson_id, value = 1) {
    user_id = getBasicInfo('user_id');
    lesson_id = lesson_id ? lesson_id : getBasicInfo('lesson_id');

    // 更新课程进度并写入 cookie
    let percent = value;
    let flag = user_id + lesson_id;
    $.cookie(flag, percent);

    // 常规课程页面: HTML5 Player
    if (url_current.indexOf(url_lesson_type_1) == 0) {
      $.ajax({
        type: 'post',
        url:
          '/www/command/LessonAction?flag=updateNormalStudyInfo&percentNum=' +
          percent +
          '&leid=' +
          lesson_id +
          '&userCode=' +
          user_id +
          '&preview=1',
      });
    }

    // 常规课程页面: LMS Service
    if (url_current.indexOf(url_lesson_type_2) == 0) {
      let data = {
        direct: 'cmiputcat',
        cmi: '',
        usid: user_id,
        leid: lesson_id,
        tlid: lesson_id,
        scoid: 'normalLesson',
        preview: 1,
        percent: percent,
        action: 'lessonAction',
      };

      $.ajax({
        type: 'post',
        url: '/disLesson/servlet/LMSService',
        error: () => {
          console.error(GM.info.script.name + '：“SCORM”的 AJAX 请求失败');
        },
        data: 'data=' + JSON.stringify(data),
      });
    }

    // 互动课程页面: SCORM API
    if (
      url_current.indexOf(url_lesson_type_3) == 0 ||
      url_current.indexOf(url_lesson_type_4) == 0
    ) {
      $.ajax({
        type: 'post',
        url: '/www/command/LessonAction?flag=upLessonRefreshSession',
      });
    }
  }

